
Vtiger_Detail_Js("OSSMailView_Detail_Js",{printMail:function(){var c=$("#subject").val();var g=$("#from_email").val();var f=$("#to_email").val();var e=$("#cc_email").val();var b=jQuery("#createdtime").val();var a=$("#content").html();var d=window.open();d.document.write("<b>"+app.vtranslate("Subject")+": "+c+"</b><br>");d.document.write("<br>"+app.vtranslate("From")+": "+g+"<br>");d.document.write(app.vtranslate("To")+" :"+f+"<br>");if(e!=null){d.document.write(app.vtranslate("CC")+": "+e+"<br>")}d.document.write(app.vtranslate("Date")+": "+b+"<br>");d.document.write("<hr/>"+a+"<br>");d.print()},},{registerEvents:function(){this._super();var a=jQuery("div.detailViewToolbar");Vtiger_Index_Js.registerMailButtons(a)}});